package 算法题;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;

/**
 * 438. 找到字符串中所有字母异位词
 * 给定一个字符串 s 和一个非空字符串 p，找到 s 中所有是 p 的字母异位词的子串，返回这些子串的起始索引。
 * 字符串只包含小写英文字母，并且字符串 s 和 p 的长度都不超过 20100。
 * 说明：
 * 字母异位词指字母相同，但排列不同的字符串。
 * 不考虑答案输出的顺序。
 * 示例 1:
 * 输入:
 * s: "cbaebabacd" p: "abc"
 * 输出:
 * [0, 6]
 * 解释:
 * 起始索引等于 0 的子串是 "cba", 它是 "abc" 的字母异位词。
 * 起始索引等于 6 的子串是 "bac", 它是 "abc" 的字母异位词。
 * 示例 2:
 * 输入:
 * s: "abab" p: "ab"
 * 输出:
 * [0, 1, 2]
 * 解释:
 * 起始索引等于 0 的子串是 "ab", 它是 "ab" 的字母异位词。
 * 起始索引等于 1 的子串是 "ba", 它是 "ab" 的字母异位词。
 * 起始索引等于 2 的子串是 "ab", 它是 "ab" 的字母异位词。
 */
public class likou438 {
    public static void main(String[] args) {
        likou438 likou = new likou438();
//        String s = "cbaebabacd", t = "abc";
//        String s = "abab", t = "ab";
//        String s = "abacbabc", t = "abc";
        String s = "",
                t = "";
        List<Integer> li = likou.findAnagrams(s, t);
        System.out.print(li);
    }

    public List<Integer> findAnagrams(String s, String t) {
        char[] need = new char[26];
        for (int i = 0; i < t.length(); i++) {
            need[t.charAt(i) - 'a']++;
        }
        int start = 0, end = 0, valid = 0;
        List<Integer> res = new ArrayList<>();
        char[] window = new char[26];
        while (end < s.length()) {
            window[s.charAt(end) - 'a']++;
            while (end - start + 1 == t.length()) {
                if (Arrays.equals(window, need)) res.add(start);
                window[s.charAt(start) - 'a']--;
                start++;
            }
            end++;
        }
        return res;
    }

    public List<Integer> findAnagrams3(String s, String p) {
        if (s.length() == 0 || p.length() == 0) return null;
        int n = s.length(), m = p.length();
        List<Integer> res = new ArrayList<>();
        if (n < m) return res;
        int[] pCnt = new int[26];
        int[] sCnt = new int[26];
        for (int i = 0; i < m; i++) {
            pCnt[p.charAt(i) - 'a']++;
            sCnt[s.charAt(i) - 'a']++;
        }
        if (Arrays.equals(sCnt, pCnt)) {
            res.add(0);
        }
        for (int i = m; i < n; i++) {
            sCnt[s.charAt(i - m) - 'a']--;
            sCnt[s.charAt(i) - 'a']++;
            if (Arrays.equals(sCnt, pCnt)) {
                res.add(i);
            }
        }
        return res;
    }
}
